<?php
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use violuke\RsaSshKeyFingerprint\FingerprintGenerator;
use violuke\RsaSshKeyFingerprint\InvalidInputException;

final class FingerprintTest extends TestCase
{
    public function testValidFingerprints()
    {
        $testCases = [
            [
                'key' => 'ssh-rsa  lukecousins@Lukes-iMac.local',
                'fingerprint' => '47:ae:96:ec:e0:b7:bc:59:69:db:24:37:56:21:8f:46',
                'sha256Fingerprint' => '+Sg6ZIRbJ4y0tqyP/N4zLhn6pwgzbH1oMKjdJZ+IWvc=',
            ],
            [
                'key' => 'ssh-rsa  lukecousins@Lukes-iMac.local',
                'fingerprint' => '74:e7:80:bf:89:11:31:55:f7:4d:78:86:0d:fd:13:f5',
                'sha256Fingerprint' => '6EbRDmqVEmgAD6FOXQuIiG8uUSteIDKZzj2bgZ+88V0=',
            ],
            [
                'key' => 'ssh-rsa ',
                'fingerprint' => 'b1:17:8b:f3:34:8a:17:c7:70:ac:dd:c9:da:b2:b6:bd',
                'sha256Fingerprint' => 'Dvxyo3sSYtSvazExp6SihBKt6BYSjbUG1NG7L3sbAq0=',
            ],
        ];

        foreach ($testCases as $case) {
            $generatedFingerprint = FingerprintGenerator::getFingerprint($case['key']);
            $this->assertEquals($case['fingerprint'], $generatedFingerprint);
            $generatedSha256Fingerprint = FingerprintGenerator::getFingerprint($case['key'], 'sha256');
            $this->assertEquals($case['sha256Fingerprint'], $generatedSha256Fingerprint);
        }
    }

    public function testInvalidInput()
    {
        $this->expectException(InvalidInputException::class);
        FingerprintGenerator::getFingerprint('rsa  lukecousins@Lukes-iMac.local');
    }

    public function testInvalidHashAlgorithm()
    {
        $this->expectException(InvalidInputException::class);
        FingerprintGenerator::getFingerprint('ssh-rsa ', 'abc');
    }
}
